mapper.select:
SELECT * FROM ( SELECT FREEIDX, TYPE, TITLE, CONTENT, FREEDATE, ID, IP, PASSWORD, ISDEL, READCOUNT, GOOD, BAD, ROWNUM R FROM ( SELECT FREEIDX, TYPE, TITLE, CONTENT, FREEDATE, ID, IP, PASSWORD, ISDEL, READCOUNT, GOOD, BAD FROM SHORT
TITLE LIKE '%'||#{KEYWORD}||'%'
ORDER BY FREEIDX DESC
ORDER BY GOOD DESC
) ) WHERE R between #{START} and #{END}

mapper.update:

UPDATE
	FREECOM
SET
	CONTENT = #{CONTENT}
WHERE
	FREECOMIDX = #{FREECOMIDX}
