mapper.select:
SELECT * FROM ( SELECT INFOIDX, TYPE, TITLE, CONTENT, INFODATE, ID, IP, PASSWORD, ISDEL, READCOUNT, GOOD, BAD, ROWNUM R FROM ( SELECT INFOIDX, TYPE, TITLE, CONTENT, INFODATE, ID, IP, PASSWORD, ISDEL, READCOUNT, GOOD, BAD FROM INFO
TITLE LIKE '%'||#{KEYWORD}||'%'
ORDER BY INFOIDX DESC
ORDER BY GOOD DESC
) ) WHERE R between #{START} and #{END}

mapper.update:

UPDATE
	INFOCOM
SET
	CONTENT = #{CONTENT}
WHERE
	INFOCOMIDX = #{INFOCOMIDX}
